import styled from "styled-components";

export const Container = styled.header`
  background: ${(props) => props.theme.colors.topBackground};
  color: red;
  max-width: 1024px;
  margin: 20px auto 0;
`;

export const Title = styled.h1`
  color: ${(props) => props.theme.colors.title};
  font-size: 24px;
`;
