import React from "react";
import BorderCard from "../BorderCard";

import { Container } from "./styles";

data = [
  {
    border: "#1CA0F2",
    src: "/images/icon-facebook.svg",
  },
  {
    border: "#00BFFF",
    src: "/images/icon-twitter.svg",
  },
  {
    border:
      "linear-gradient(to right, var(--instagram) 0%, var(--instagram-2) 100%)",
    src: "/images/icon-instragram.svg",
  },
];

const Summary = (props) => {
  return (
    <Container>
      {data.map((el) => (
        <Card {...el} />
      ))}
    </Container>
  );
};
export default Summary;
